import { Component, Input } from '@angular/core';

@Component({
  selector: 'f1-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss'],
})
export class CardComponent {
  @Input() year: string = '';
  @Input() name: string = '';
  @Input() isChampion: boolean = false;

}

<div class="card" [class.champion]="isChampion">
    <h3>{{year}}</h3>
    <h1>{{name}}</h1> 
    <h2 *ngIf="isChampion">World Champion of the Season</h2>    
</div>
